#include <stdio.h> // Includes the input/output library

int main() { // Defines the main function
  printf("Hello, World!\n") // Outputs Hello, World! and a newline

  return 0; // indicate the program executed successfully
}

int factorial(int x) {
  int y = 1;

  for (int i = 2; i <= x; i++)
    y *= i;

  return y;
}

// Usage:
factorial(4); // Returns: 24

#include<stdio.h>
void swap(int* num1,int* num2){
    *num1 = *num1 + *num2;
    *num2 = *num1 - *num2;
    *num1 = *num1 - *num2;
}

// Usage:
int a = 3,b = 4;
swap(&a,&b); // swaps the values of the a and b variables
