def find_duplicates(lst):
    seen = set()
    duplicates = set()
    for item in lst:
        if item in seen:
            duplicates.add(item)
        else:
            seen.add(item)
    return list(duplicates)

# Usage:
data = [1, 2, 3, 2, 4, 5, 1]
print(find_duplicates(data))  # Output: [1, 2]

def max_difference(lst):
    if not lst or len(lst) < 2:
        return 0
    return max(lst) - min(lst)

# Usage:
